class NoteModel {  final int? id;  final String title;  final String description;  final DateTime? createdAt;  final DateTime? updatedAt;  const NoteModel({    this.id,    required this.title,    required this.description,    this.createdAt,    this.updatedAt  });  factory NoteModel.fromJson(Map<String, dynamic> json) {    return NoteModel(      id: json['id'],      title: json['title'],      description: json['description'],      createdAt: json['createdAt'],      updatedAt: json['updatedAt'],    );  }}