import 'package:flutter/material.dart';import 'package:rx_dart_notes/blocs/note_bloc.dart';import '../core/models/note_model.dart';class DeleteNotesPage extends StatefulWidget {  final NoteModel? note;  const DeleteNotesPage({    Key? key,    this.note  }) : super(key: key);  @override  State<DeleteNotesPage> createState() => _DeleteNotesPageState();}class _DeleteNotesPageState extends State<DeleteNotesPage> {  @override  Widget build(BuildContext context) {    return AlertDialog(      title: const Text('Attention'),      content: const Text('ÃŠtes-vous sure de vouloir supprimer cette note'),      actions: <Widget>[        FloatingActionButton(          onPressed: () {           bloc.deleteNote(widget.note!);           Navigator.of(context).pop(true);          },          child: const Text('OUI'),        ),        FloatingActionButton(          onPressed: () {            Navigator.of(context).pop(false);          },          child: const Text('NON'),        )      ],    );  }}